example.programlisting:
function get_input() {
    global $entry;
$input=$entry->get_text();
print "$input\n";
$entry->grab_focus();
$entry->set_text("");
}

$window = &new GtkWindow();
$window->set_position(GTK_WIN_POS_MOUSE);
$window->connect_object("destroy", array("gtk", 
"main_quit"));

$entry = &new GtkEntry();
$window->add($entry);
$entry->connect("activate", "get_input");
$entry->show();

$window->show_all();

gtk::main();
